import React from 'react';
import {
    StyleSheet,
    Text,
    View,
    Modal,
    Pressable,
    Image,
} from 'react-native';

import { TouchableOpacity } from 'react-native-gesture-handler';
import { FlatList } from 'react-native-gesture-handler';
import { AsyncStorage } from '@react-native-async-storage/async-storage';
import { Component } from 'react/cjs/react.production.min';
import { AppRegistry } from 'react-native-web';

export default class ListagemCurso extends Component {
    constructor(props) {
        super(props);
        this.state = {
            cursoBuscado: 0,
            modalVisivel: false,
            listaCurso: [],
        };
    }

    ListarCurso = async () => {
        try {
            const token = await AsyncStorage.getItem('userToken')

            const resposta = await AppRegistry('/Cursos',
                {
                    headers: {
                        Authorization: 'Bearer ' + token,
                    }
                },
            );
            if (resposta.status == 200) {
                const dadosCurso = resposta.data;
                this.setState({ listaCurso: dadosCurso })
                console.warn('Cursos encontrados');
                console.warn(this.state.listaCurso);
            }
        }
        catch (erro) {
            console.warn(erro);
        }
    }
    setModalVisivel = (visible) => {
        this.setState({ modalVisivel: visible })
    }
    componentDidMount() {
        this.ListarCurso();
    }

    render() {
        return (
            <View style={styles.containerListagem}>
                <View style={styles.boxLogoHeader}>
                    <Image source={require('../../../assets/imgGP2/logo_2S.png')} />
                </View>

                <View style={styles.boxTituloPrincipal}>
                    <Text style={styles.textTituloPrincipal}>cursos</Text>
                </View>

                <View style={styles.boxCurso}>
                    <View style={styles.boxImgCurso}>
                        <Image source={require('../../../assets/imgGP2/imgCurso.png')} />
                    </View>

                    <View style={styles.boxTituloCurso}>
                        <Text style={styles.textTituloCurso}>Lógica de Programação</Text>
                    </View>

                    <View style={styles.boxAvaliacao}>
                        <Image source={require('../../../assets/imgGP2/estrelaCheia.png')} />
                        <Image source={require('../../../assets/imgGP2/estrelaCheia.png')} />
                        <Image source={require('../../../assets/imgGP2/estrelaCheia.png')} />
                        <Image source={require('../../../assets/imgGP2/estrelaCheia.png')} />
                        <Image source={require('../../../assets/imgGP2/estrelaVazia.png')} />
                    </View>

                    <View style={styles.boxDadosCurso}>
                        <View style={styles.boxDados}>
                            <Image source={require('../../../assets/imgGP2/relogio.png')} />
                            <Text style={styles.textDados}>20 horas</Text>
                        </View>

                        <View style={styles.boxDados}>
                            <Image source={require('../../../assets/imgGP2/local.png')} />
                            <Text style={styles.textDados}>EAD</Text>
                        </View>
                    </View>

                    <View style={styles.boxPrecoFavorito}>
                        <View style={styles.boxPreco}>
                            <Image source={require('../../../assets/imgGP2/cash.png')} />
                            <Text style={styles.textDados}>1024</Text>
                        </View>

                        <View style={styles.boxFavorito}>
                            <Image source={require('../../../assets/imgGP2/coracaoVazio.png')} />
                        </View>
                    </View>

                </View>
            </View>
        )
    }
}
const styles = StyleSheet.create({
    containerListagem: {
        flex: 1,
        alignItems: 'center'
    },
    boxLogoHeader: {
        marginTop: 50
    },
    boxTituloPrincipal: {
        marginTop: 30,
        marginBottom: 30
    },
    textTituloPrincipal: {
        textTransform: 'uppercase',
        fontSize: 30
    },
    boxCurso: {
        height: 280,
        borderWidth: 2,
        borderColor: '#B3B3B3',
        borderRadius: 10,
    },
    boxTituloCurso: {
        marginLeft: 16
    },
    textTituloCurso: {
        fontSize: 20,
        marginTop: 8,
    },
    boxAvaliacao: {
        width: 150,
        height: 30,
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginTop: 8,
        marginLeft: 16,
    },
    boxDados: {
        display: 'flex',
        flexDirection: 'row',
        marginTop: 8,
        marginLeft: 16
    },
    textDados: {
        marginLeft: 8
    },
    boxPrecoFavorito: {
        display: 'flex',
        flexDirection: 'row',
        marginTop: 16,
        marginLeft: 16
    },
    boxPreco: {
        width: 85,
        borderWidth: 2,
        borderColor: '#B3B3B3',
        borderRadius: 10, 
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center'
    },
    boxFavorito: {
        marginLeft: 150
    }
})
